import logo from './logo.svg';
import './App.css';

import {ReactComponent as Brain} from './assets/brain.svg';

import ScrollingText from './components/ScrollingText/ScrollingText';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Brain className="App-logo" />



        <ScrollingText text="" anDur={2} size={1} />
        <ScrollingText anDur={3} size={1.5}/>
        <ScrollingText text="\\( e^{i\\pi} + 1 = 0 \\)" anDur={4} size={2}/>
        <ScrollingText anDur={5} size={2.5}/>
        <ScrollingText anDur={6} size={3}/>
        <ScrollingText anDur={7} size={3.5}/>

      </header>
    </div>
  );
}

export default App;
